import React, { useState, useEffect, useRef } from 'react';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [place, setPlace] = useState('Lobby');
  const [myPlayerId, setMyPlayerId] = useState();
  const [gameState, setGameState] = useState(null);
  const ws = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket('ws://localhost:7777/ws/');
    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.MessageType===1) {
        setRooms(data.Rooms);
        setPlace('Lobby')
      }
      else if(data.MessageType===2){
        setRooms([]);
        const place = "Room " + data.RoomId
        setPlace(place)
        setGameState(data)
      }
      else if(data.MessageType===3){
        setMyPlayerId(data.PlayerId)
      }
      setMessages((prevMessages) => [...prevMessages, event.data]);
    };

    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []);
  const currentPlayer = gameState.PlayersInfo.find(player => player.PlayerId === myPlayerId);
  const otherPlayers = gameState.PlayersInfo.filter(player => player.PlayerId !== myPlayerId);
  const renderHandTiles = (handTiles) => {
    return handTiles.map((tile, index) => (
      <img
        key={index}
        src={`/tiles/${tile._tileType}_${tile._tileNumber}.png`}
        alt={`Tile ${tile._tileType}-${tile._tileNumber}`}
        style={{ width: '50px', height: '75px', margin: '2px' }}
      />
    ));
  };

  const renderOtherPlayerHand = (player) => {
    return (
      <div>
        <h4>Player {player.Seat}</h4>
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          {player.HandTiles.map((tile, index) => (
            <div
              key={index}
              style={{
                width: '50px',
                height: '75px',
                backgroundColor: 'gray',
                margin: '2px',
              }}
            />
          ))}
        </div>
      </div>
    );
  };
  const renderLoading = () =>{
    return <div>Loading...</div>;
  }
  const renderTable = () => {
    const playerBySeat = {};
    PlayersInfo.forEach((player) => {
      playerBySeat[player.Seat] = player;
    });

    // Dynamic rendering based on player's seat
    const seatOrder = {
      left: playerBySeat[(currentPlayer.Seat + 3) % 4] || null,
      top: playerBySeat[(currentPlayer.Seat + 2) % 4] || null,
      right: playerBySeat[(currentPlayer.Seat + 1) % 4] || null,
    };

    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '500px', position: 'relative' }}>
        {/* Top player */}
        <div style={{ position: 'absolute', top: '0', display: 'flex', justifyContent: 'center' }}>
          {seatOrder.top && renderOtherPlayerHand(seatOrder.top)}
        </div>

        {/* Left player */}
        <div style={{ position: 'absolute', left: '0', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
          {seatOrder.left && renderOtherPlayerHand(seatOrder.left)}
        </div>

        {/* Right player */}
        <div style={{ position: 'absolute', right: '0', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
          {seatOrder.right && renderOtherPlayerHand(seatOrder.right)}
        </div>

        {/* Center (played tiles area) */}
        <div style={{ position: 'absolute', width: '200px', height: '200px', backgroundColor: '#ddd', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <h3>Played Tiles</h3>
          {/* Render played tiles here */}
        </div>

        {/* Bottom (current player) */}
        <div style={{ position: 'absolute', bottom: '0', display: 'flex', justifyContent: 'center' }}>
          <div>{renderHandTiles(currentPlayer.HandTiles)}</div>
        </div>
      </div>
    );
  };

  const createRoom = () => {
    if ( ws.current.readyState === WebSocket.OPEN) {
      const msg = "1|";
      ws.current.send(msg);
      console.log(`Message sent: ${msg}`);
    }
  };
  const joinRoom = (room) => {
    const message = `2|${room}`; 
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(message); // Send the message via WebSocket
      console.log(`Message sent: ${message}`);
    } else {
      console.log('WebSocket is not open');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ marginBottom: '10px' }}>
        <h1>Your ID: {myPlayerId}; Place: {place}</h1>
        <button onClick={createRoom}>Create Room</button>
      </div>
      <div>
      <h1>Mahjong Game</h1>
      {place.startsWith("Room")?renderTable():renderLoading()}
      </div>
      <div style={{ padding: '20px' }}>
      <h1>Room List</h1>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        {rooms.map((room, index) => (
          <div
            key={index}
            onClick={() => joinRoom(room)}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#45a049')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#4CAF50')}
          >
            Room {room}
          </div>
        ))}
      </div>
    </div>
      <div>
        <h2>Received Messages</h2>
        <div style={{ border: '1px solid black', padding: '10px', height: '200px', overflowY: 'scroll' }}>
          {messages.length > 0 ? (
            messages.map((msg, index) => <p key={index}>{msg}</p>)
          ) : (
            <p>No messages received yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
