import React, { useState, useEffect, useRef } from 'react';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [place, setPlace] = useState('Lobby');
  // const [inputMessage, setInputMessage] = useState('');
  const ws = useRef(null); // WebSocket reference to persist connection

  useEffect(() => {
    ws.current = new WebSocket('ws://localhost:7777/ws/');
    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.MessageType===1) {
        setRooms(data.Rooms);
        setPlace('Lobby')
      }
      else if(data.MessageType===2){
        setRooms([]);
        setPlace('Room')

      }
      setMessages((prevMessages) => [...prevMessages, event.data]);
    };

    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []);

  const createRoom = () => {
    if ( ws.current.readyState === WebSocket.OPEN) {
      const msg = "1|";
      ws.current.send(msg);
      console.log(`Message sent: ${msg}`);
      // setInputMessage(''); // Clear input after sending message
    }
  };
  const handleRoomClick = (room) => {
    const message = `2|${room}`; // Create message in the format "2|{room number}"
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(message); // Send the message via WebSocket
      console.log(`Message sent: ${message}`);
    } else {
      console.log('WebSocket is not open');
    }
  };
  return (
    <div style={{ padding: '20px' }}>
      <div style={{ marginBottom: '10px' }}>
        <h1>Place: {place}</h1>
        <button onClick={createRoom}>Create Room</button>
      </div>
      <div style={{ padding: '20px' }}>
      <h1>Room List</h1>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        {rooms.map((room, index) => (
          <div
            key={index}
            onClick={() => handleRoomClick(room)}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#45a049')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#4CAF50')}
          >
            Room {room}
          </div>
        ))}
      </div>
    </div>
      <div>
        <h2>Received Messages</h2>
        <div style={{ border: '1px solid black', padding: '10px', height: '200px', overflowY: 'scroll' }}>
          {messages.length > 0 ? (
            messages.map((msg, index) => <p key={index}>{msg}</p>)
          ) : (
            <p>No messages received yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
