import React, { useState, useEffect, useRef } from 'react';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const ws = useRef(null); // WebSocket reference to persist connection

  useEffect(() => {
    // Create WebSocket connection.
    ws.current = new WebSocket('ws://localhost:7777/ws/');

    // Listen for messages from the WebSocket server.
    ws.current.onmessage = (event) => {
      setMessages((prevMessages) => [...prevMessages, event.data]);
    };

    // Clean up the WebSocket connection when the component unmounts.
    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []);

  const sendMessage = () => {
    if (inputMessage.trim() && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(inputMessage);
      setInputMessage(''); // Clear input after sending message
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>WebSocket Chat</h1>
      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Enter your message"
        />
        <button onClick={sendMessage}>Send Message</button>
      </div>
      <div>
        <h2>Received Messages</h2>
        <div style={{ border: '1px solid black', padding: '10px', height: '200px', overflowY: 'scroll' }}>
          {messages.length > 0 ? (
            messages.map((msg, index) => <p key={index}>{msg}</p>)
          ) : (
            <p>No messages received yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
