import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [socket, setSocket] = useState(null); // 存儲 WebSocket 實例
  const [messages, setMessages] = useState([]); // 存儲接收到的消息
  const [inputValue, setInputValue] = useState(''); // 存儲用戶輸入

  // 初始化 WebSocket 連接
  useEffect(() => {
    const ws = new WebSocket('ws://localhost:7777'); // 替換為你的 WebSocket 伺服器地址

    // 打開 WebSocket 連接
    ws.onopen = () => {
      console.log('Connected to WebSocket');
      setSocket(ws);
    };

    // 處理接收到的消息
    ws.onmessage = (event) => {
      setMessages((prevMessages) => [...prevMessages, event.data]);
    };

    // 處理連接關閉
    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    // 處理錯誤
    ws.onerror = (error) => {
      console.log('WebSocket error', error);
    };

    // 清理 WebSocket 連接
    return () => {
      ws.close();
    };
  }, []);

  // 發送消息到 WebSocket 伺服器
  const sendMessage = () => {
    if (socket && inputValue !== '') {
      socket.send(inputValue);
      setMessages((prevMessages) => [...prevMessages, `Sent: ${inputValue}`]);
      setInputValue(''); // 清空輸入框
    }
  };

  return (
    <div className="App">
      <h1>WebSocket Chat</h1>

      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index}>{msg}</div>
        ))}
      </div>

      <div className="inputArea">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter message"
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
